import React from 'react';
import './messages.css';

const NoCounters = () => {
	return <h1 className="intro-text">Create your first counter!</h1>;
};

const NoMatchingItems = ({ onGoBack }) => {
	return (
		<div className="error">
			<p>Sorry, there are no counters that match the specified conditions :(</p>
			<span className="link" onClick={onGoBack}>
				Go back
			</span>
		</div>
	);
};

const SuccessfullyUpdated = ({ item }) => {
	const { name, type } = item;
	return (
		<div className="success">
			<p>
				{name} was successfully {type === 'add' ? 'added to' : 'deleted from'} your list
			</p>
		</div>
	);
};

export { NoCounters, NoMatchingItems, SuccessfullyUpdated };
