import React from 'react';
import './app.css';
import injectContext, { Consumer } from '../store/appContext';
import CounterHolder from './counterHolder/counterHolder';
import Header from './header/header';

const App = () => {
	return (
		<Consumer>
			{({ store, actions }) => {
				const { counters, sort } = store;
				const total = counters.map(c => c.count).reduce((acc, val) => acc + val);
				const items = counters;

				let asc = items.sort((a, b) => {
					if (a[sort.column] > b[sort.column]) {
						return 1;
					}
					else if (a[sort.column] < b[sort.column]) {
						return -1;
					}
					return 0;
				});	
				let desc = items.sort((a, b) => {
					if (b[sort.column] > a[sort.column]) {
						return 1;
					}
					else if (b[sort.column] < a[sort.column]) {
						return -1;
					}
					return 0;
				});	

				console.log('asc', asc, 'desc', desc);


				return (
				<>
				<Header total={total} actions={actions} />
				<CounterHolder items={sort.order === 'asc' ? asc : desc} actions={actions} />
				</>)
			}}
		</Consumer>
	);
};

export default injectContext(App);
