import React from 'react';
import './filters.css';

const Filters = ({ actions }) => {
	return (
		<div className="filters">
			<h6>Filter your results:</h6>
			<label htmlFor="less">Less than:</label>
			<input type="number" name="less" onChange={(e) => actions.handleFilter(e.target.name, e.target.value)} />

			<input
				type="number"
				name="greater"
				placeholder="Greater than:"
				onChange={(e) => actions.handleFilter(e.target.name, e.target.value)}
			/>
		</div>
	);
};

export default Filters;
