import React from 'react';
import './app.css';
import injectContext, { Consumer } from '../../store/appContext';
import CounterHolder from '../counterHolder/counterHolder';
import Header from '../header/header';

const App = () => {
	return (
		<Consumer>
			{({ store, actions }) => {
				let total = counters.map(c => c.count).reduce((acc, val) => acc + val);

				return (
				<>
				<Header total={total} />
				<CounterHolder items={store.counters} actions={actions} />
				</>)
			}}
		</Consumer>
	);
};

export default injectContext(App);
