import React, { useState } from 'react';

const CounterGenerator = ({ onNewCounter }) => {
	const [ name, setName ] = useState('');

	const handleInput = (e) => {
		const value = e.target.value;
		setName(value);
	};

	return (
		<form
			onSubmit={(e) => {
				e.preventDefault();
				setName('');
				onNewCounter(name);
			}}
		>
			<label htmlFor="name">Add a new counter: </label>
			<input name="name" value={name} type="text" onChange={(e) => handleInput(e)} />
		</form>
	);
};

export default CounterGenerator;
