import Axios from 'axios';
import _ from 'lodash';

const apiEndpoint = '/api/v1/counter';

const getState = ({ getStore, getActions, setStore }) => {
	return {
		store: {
			counters: [],
			sortedCounters: [],
			sort: {
				column: '',
				order: 'desc',
				active: true
			},
			query: '',
			filter: {
				less: false,
				lessQuery: '',
				greater: false,
				greaterQuery: ''
			}
		},
		actions: {
			getCounters: async () => {
				const response = await Axios.get(`${apiEndpoint}s`);
				const counters = response.data;
				setStore({ counters });
			},
			handleNewCounter: async (title) => {
				if (title.trim() !== '') {
					const store = getStore();

					const obj = { title };
					const { data: counter } = await Axios.post(apiEndpoint, obj);
					const counters = sortedCounters.length
						? [ ...store.sortedCounters, counter ]
						: [ ...store.counters, counter ];

					setStore({ counters });
				}
			},
			handleAddition: async (item) => {
				const store = getStore();
				const sort = store.sort;
				sort.active = false;

				const counters = sortedCounters.length ? [ ...store.sortedCounters ] : [ ...store.counters ];
				const index = counters.indexOf(item);

				const obj = { id: item.id };
				const { data: counter } = await Axios.post(`${apiEndpoint}/inc`, obj);
				counters[index] = { ...counter };

				setStore({ sort, counters });
			},
			handleSubstraction: async (item) => {
				const store = getStore();
				const sort = store.sort;
				sort.active = false;

				const counters = sortedCounters.length ? [ ...store.sortedCounters ] : [ ...store.counters ];
				const index = counters.indexOf(item);

				const obj = { id: item.id };
				const { data: counter } = await Axios.post(`${apiEndpoint}/dec`, obj);
				counters[index] = { ...counter };

				setStore({ sort, counters });
			},
			handleDelete: async (item) => {
				const store = getStore();
				const counters = sortedCounters.length ? [ ...store.sortedCounters ] : [ ...store.counters ];
				counters = counters.filter((c) => c !== item);
				setStore({ counters });

				const obj = { id: item.id };
				await Axios.delete(apiEndpoint, { data: obj });
			},
			handleSort: (selectedColumn) => {
				const store = getStore();
				let sort = { ...store.sort };
				let sortedCounters = sortedCounters.length ? [ ...store.sortedCounters ] : [ ...store.counters ];

				if (sort.column === selectedColumn) {
					sort.order = sort.order === 'asc' ? 'desc' : 'asc';
					sort.active = true;
				} else {
					sort.column = selectedColumn;
					sort.order = 'asc';
					sort.active = true;
				}

				sortedCounters = sort.active
					? _.orderBy(sortedCounters, [ sort.column ], [ sort.order ])
					: sortedCounters;

				setStore({ sort, sortedCounters });
			},
			handleSearch: (query) => {
				const store = getStore();
				let sortedCounters = sortedCounters.length ? [ ...store.sortedCounters ] : [ ...store.counters ];

				if (query.trim() !== '') {
					sortedCounters = sortedCounters.filter((c) => c.title.toLowerCase().includes(query.toLowerCase()));
					setStore({ query, sortedCounters });
				} else {
					setStore({ query: '', sortedCounters });
				}
			},
			handleFilter: (type, queryType, number) => {
				const store = getStore();
				let sortedCounters = sortedCounters.length ? [ ...store.sortedCounters ] : [ ...store.counters ];

				const filter = { ...store.filter };
				let query = parseInt(number);
				query = Number.isNaN(query) ? '' : query;

				if (query !== '') {
					filter[type] = true;
					filter[queryType] = query;
					if (queryType === 'less') {
						sortedCounters = sortedCounters.filter((i) => i.count < filter.lessQuery);
					} else {
						sortedCounters = sortedCounters.filter((i) => i.count > filter.lessQuery);
					}
				} else {
					filter[type] = false;
					filter[queryType] = '';
				}

				setStore({ filter, sortedCounters });
			},
			cleanFilter: (type, queryType) => {
				const store = getStore();
				let filter = { ...store.filter };
				filter[type] = false;
				filter[queryType] = '';
				setStore({ filter });
			},
			cleanSearch: () => {
				const store = getStore();
				let filter = { ...store.filter };

				if (filter.less) {
					filter.less = false;
					filter.lessQuery = '';
				} else if (filter.greater) {
					filter.greater = false;
					filter.greaterQuery = '';
				}

				setStore({ query: '', filter });
			}
		}
	};
};

export default getState;
