import React, { useState } from 'react';
import './filters.css';

const Filters = ({ actions }) => {
	const [ display, setDisplay ] = useState('hide');

	return (
		<div className="filters">
			<h6 onClick={setDisplay('show')}>Filter your results:</h6>
			<div>
				<span>
					<label htmlFor="less">Less than: </label>
					<input
						className="sm-input"
						type="number"
						name="less"
						onChange={(e) => actions.handleFilter(e.target.name, e.target.value)}
					/>
				</span>

				<label htmlFor="greater">Greater than: </label>
				<input
					className="sm-input"
					type="number"
					name="greater"
					onChange={(e) => actions.handleFilter(e.target.name, e.target.value)}
				/>
			</div>
		</div>
	);
};

export default Filters;
