import React from 'react';

const Filters = ({ actions }) => {
	return (
		<>
			<input
				type="number"
				name="less"
				placeholder="Less than:"
				onChange={(e) => actions.handleFilter(e.target.name, e.target.value)}
			/>

			<input
				type="number"
				name="greater"
				placeholder="Greater than:"
				onChange={(e) => actions.handleFilter(e.target.name, e.target.value)}
			/>
		</>
	);
};

export default Filters;
