import React from 'react';
import './app.css';
import injectContext, { Consumer } from '../store/appContext';
import CounterHolder from './counterHolder/counterHolder';
import Header from './header/header';

const App = () => {
	return (
		<Consumer>
			{({ store, actions }) => {
				const { counters, sort } = store;
				const total = counters.map(c => c.count).reduce((acc, val) => acc + val);
				let sorted = counters;

				if (sort.column !== '') {
					let desc = [ ...sorted ];
					let asc = sorted.sort((a, b) => {
						if (a[sort.column] > b[sort.column]) {
							return 1;
						}
						if (a[sort.column] < b[sort.column]) {
							return -1;
						}
						return 0;
					});	
					sorted = sort.order === 'asc' ? asc : desc;
				} else {
					sorted = sorted;
				}

				return (
				<>
				<Header total={total} actions={actions} />
				<CounterHolder items={sorted} actions={actions} />
				</>)
			}}
		</Consumer>
	);
};

export default injectContext(App);
