import Axios from 'axios';

const apiEndpoint = '/api/v1/counters';

const getState = ({ getStore, getActions, setStore }) => {
	return {
		store: {
			counters: [],
			sort: {
				column: '',
				order: '',
				active: false
			},
			query: '',
			filter: {
				type: '',
				number: ''
			}
		},
		actions: {
			getCounters: async () => {
				const response = await axios.get(apiEndpoint);
				setStore({ counters: response.data });
			},
			handleNewCounter: (title) => {
				if (title.trim() !== '') {
					const store = getStore();
					const newCounter = {
						title,
						count: 0
					};
					const counters = [ ...store.counters, newCounter ];
					setStore({ counters });

					const counter = { title };
					Axios.post();
				}
			},
			handleAddition: (item) => {
				const store = getStore();
				const counters = [ ...store.counters ];
				const index = counters.indexOf(item);
				counters[index] = { ...item };
				counters[index].count += 1;

				setStore({ counters });
			},
			handleSubstraction: (item) => {
				const store = getStore();
				const counters = [ ...store.counters ];
				const index = counters.indexOf(item);
				counters[index] = { ...item };
				counters[index].count -= 1;

				setStore({ counters });
			},
			handleDelete: (item) => {
				const store = getStore();
				let counters = [ ...store.counters ];
				counters = counters.filter((c) => c !== item);
				console.log(counters);
				setStore({ counters });
			},
			handleSort: (selectedColumn) => {
				const store = getStore();
				let sort = { ...store.sort };

				if (sort.column === selectedColumn) {
					sort.order = sort.order === 'asc' ? 'desc' : 'asc';
					sort.active = true;
				} else {
					sort.column = selectedColumn;
					sort.order = 'asc';
					sort.active = false;
				}

				setStore({ sort });
			},
			handleSearch: (query) => {
				if (query.trim() !== '') {
					setStore({ query });
				} else {
					setStore({ query: '' });
				}
			},
			handleFilter: (type, amount) => {
				let number;
				if (amount !== '') {
					number = parseInt(amount);
				} else {
					number = '';
				}
				setStore({ filter: { type, number } });
			}
		}
	};
};

export default getState;
